css:
.modal-enter {
  opacity: 0;
  transform: scale(0.95);
  animation: modalenter 0.3s cubic-bezier(0.0, 0.0, 0.2, 1) forwards;
}

@keyframes modalenter {
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.modal-exit {
  opacity: 1;
  transform: scale(1);
  animation: modalexit 0.3s cubic-bezier(0.0, 0.0, 0.2, 1) forwards;
}

@keyframes modalexit {
  to {
    opacity: 0;
    transform: scale(0.95);
  }
}
